<?php/** * View User Details Page *  * Displays detailed information about a specific user. */// Include core functionalityrequire_once 'user_management_system.php';// Check if admin is logged inif (!isAdminLoggedIn()) {    redirectToLogin();}// Check if user ID is providedif (!isset($_GET['id']) || empty($_GET['id'])) {    header("Location: user_list.php");    exit();}$userId = (int) $_GET['id'];$user = getUserDetails($userId);// If user not found, redirect back to user listif (!$user) {    $_SESSION['error_message'] = "User not found";    header("Location: user_list.php");    exit();}// Process user deletion if form is submitted$deleteMessage = '';$deleteSuccess = false;if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['delete_user'])) {    $adminPassword = $_POST['admin_password'];        $result = processUserDeletion($userId, $adminPassword);    $deleteSuccess = $result['success'];    $deleteMessage = $result['message'];        if ($deleteSuccess) {        $_SESSION['success_message'] = "User has been successfully deleted";        header("Location: user_list.php");        exit();    }}// Process user activation/deactivationif ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['toggle_status'])) {    $newStatus = ($user['is_active'] == 1) ? 0 : 1;        if (setUserActiveStatus($userId, $newStatus)) {        $statusMessage = $newStatus ? 'User activated successfully' : 'User deactivated successfully';        $user['is_active'] = $newStatus; // Update user status in current view    }}?><!DOCTYPE html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1.0">    <title>User Details - HirayaFit Admin</title>    <link rel="icon" href="images/hf.png">    <!-- Bootstrap CSS -->    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.2.3/dist/css/bootstrap.min.css" rel="stylesheet">    <!-- Font Awesome -->    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.2.1/css/all.min.css">    <style>        .profile-image {            width: 150px;            height: 150px;            object-fit: cover;            border-radius: 50%;            border: 3px solid #dee2e6;        }        .profile-header {            background-color: #f8f9fa;            border-radius: 10px;            padding: 20px;        }        .user-details {            background-color: #fff;            border-radius: 10px;            box-shadow: 0 0 10px rgba(0,0,0,0.1);        }        .detail-item {            padding: 10px;            border-bottom: 1px solid #eee;        }        .detail-item:last-child {            border-bottom: none;        }    </style></head><body><!-- Admin Header/Navbar (Include your existing admin header) --><nav class="navbar navbar-expand-lg navbar-dark bg-dark">    <div class="container">        <a class="navbar-brand" href="admin_dashboard.php">HirayaFit Admin</a>        <!-- Add your navbar items here -->    </div></nav><div class="container-fluid py-4">    <div class="row">        <!-- Sidebar (Include your existing admin sidebar) -->        <div class="col-md-3 col-lg-2 d-md-block bg-light sidebar collapse">            <!-- Your sidebar content -->        </div>                <!-- Main Content -->        <main class="col-md-9 ms-sm-auto col-lg-10 px-md-4">            <div class="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">                <h1 class="h2">User Details</h1>                <div class="btn-toolbar mb-2 mb-md-0">                    <a href="user_list.php" class="btn btn-sm btn-outline-secondary">                        <i class="fas fa-arrow-left me-1"></i> Back to Users                    </a>                </div>            </div>                        <?php if (!empty($deleteMessage)): ?>                <div class="alert alert-<?php echo $deleteSuccess ? 'success' : 'danger'; ?> alert-dismissible fade show" role="alert">                    <?php echo $deleteMessage; ?>                    <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>                </div>            <?php endif; ?>                        <?php if (isset($statusMessage)): ?>                <div class="alert alert-success alert-dismissible fade show" role="alert">                    <?php echo $statusMessage; ?>                    <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>                </div>            <?php endif; ?>                        <div class="row mb-4">                <div class="col-md-12">                    <div class="card">                        <div class="card-body">                            <div class="profile-header d-flex flex-column flex-md-row align-items-center mb-4">                                <div class="text-center me-md-4 mb-3 mb-md-0">                                    <?php if (!empty($user['profile_image']) && file_exists('uploads/profiles/' . $user['profile_image'])): ?>                                        <img src="uploads/profiles/<?php echo $user['profile_image']; ?>" alt="Profile" class="profile-image">                                    <?php else: ?>                                        <img src="assets/images/default-avatar.png" alt="Default Profile" class="profile-image">                                    <?php endif; ?>                                </div>                                <div>                                    <h3 class="mb-1"><?php echo htmlspecialchars($user['fullname']); ?></h3>                                    <p class="text-muted mb-2">@<?php echo htmlspecialchars($user['username']); ?></p>                                    <div>                                        <?php if ($user['is_active']): ?>                                            <span class="badge bg-success">Active</span>                                        <?php else: ?>                                            <span class="badge bg-danger">Inactive</span>                                        <?php endif; ?>                                    </div>                                    <div class="mt-3">                                        <button type="button" class="btn btn-sm btn-warning me-2" data-bs-toggle="modal" data-bs-target="#toggleStatusModal">                                            <i class="fas <?php echo $user['is_active'] ? 'fa-ban' : 'fa-check'; ?> me-1"></i>                                            <?php echo $user['is_active'] ? 'Deactivate Account' : 'Activate Account'; ?>                                        </button>                                        <button type="button" class="btn btn-sm btn-danger" data-bs-toggle="modal" data-bs-target="#deleteModal">                                            <i class="fas fa-trash me-1"></i> Delete Account                                        </button>                                    </div>                                </div>                            </div>                                                        <h4 class="border-bottom pb-2 mb-3">User Information</h4>                                                        <div class="row user-details p-3">                                <div class="col-md-6">                                    <div class="detail-item">                                        <strong><i class="fas fa-id-card me-2"></i>User ID:</strong>                                        <span class="ms-2"><?php echo $user['id']; ?></span>                                    </div>                                    <div class="detail-item">                                        <strong><i class="fas fa-user me-2"></i>Full Name:</strong>                                        <span class="ms-2"><?php echo htmlspecialchars($user['fullname']); ?></span>                                    </div>                                    <div class="detail-item">                                        <strong><i class="fas fa-at me-2"></i>Username:</strong>                                        <span class="ms-2"><?php echo htmlspecialchars($user['username']); ?></span>                                    </div>                                    <div class="detail-item">                                        <strong><i class="fas fa-envelope me-2"></i>Email:</strong>                                        <span class="ms-2"><?php echo htmlspecialchars($user['email']); ?></span>                                    </div>                                </div>                                <div class="col-md-6">                                    <div class="detail-item">                                        <strong><i class="fas fa-phone me-2"></i>Phone:</strong>                                        <span class="ms-2"><?php echo !empty($user['phone']) ? htmlspecialchars($user['phone']) : 'Not provided'; ?></span>                                    </div>                                    <div class="detail-item">                                        <strong><i class="fas fa-map-marker-alt me-2"></i>Address:</strong>                                        <span class="ms-2"><?php echo !empty($user['address']) ? htmlspecialchars($user['address']) : 'Not provided'; ?></span>                                    </div>                                    <div class="detail-item">                                        <strong><i class="fas fa-clock me-2"></i>Last Login:</strong>                                        <span class="ms-2"><?php echo formatDateTime($user['last_login']); ?></span>                                    </div>                                    <div class="detail-item">                                        <strong><i class="fas fa-calendar-alt me-2"></i>Registered:</strong>                                        <span class="ms-2"><?php echo formatDateTime($user['created_at']); ?></span>                                    </div>                                </div>                            </div>                        </div>                    </div>                </div>            </div>        </main>    </div></div><!-- Delete User Modal --><div class="modal fade" id="deleteModal" tabindex="-1" aria-labelledby="deleteModalLabel" aria-hidden="true">    <div class="modal-dialog">        <div class="modal-content">            <div class="modal-header bg-danger text-white">                <h5 class="modal-title" id="deleteModalLabel">Confirm User Deletion</h5>                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>            </div>            <form method="POST" action="">                <div class="modal-body">                    <div class="alert alert-warning">                        <i class="fas fa-exclamation-triangle me-2"></i>                        Are you sure you want to delete user: <strong><?php echo htmlspecialchars($user['fullname']); ?></strong>?                        <p class="mt-2 mb-0">This action cannot be undone. All user data will be permanently removed.</p>                    </div>                                        <div class="mb-3">                        <label for="admin-password" class="form-label">Enter Admin Password to Confirm:</label>                        <input type="password" class="form-control" id="admin-password" name="admin_password" required>                        <div class="form-text">For security purposes, please enter your admin password to confirm this action.</div>                    </div>                </div>                <div class="modal-footer">                    <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Cancel</button>                    <button type="submit" name="delete_user" class="btn btn-danger">Delete User</button>                </div>            </form>        </div>    </div></div><!-- Toggle Status